define((state, payload) => {
  const player = state.players.find((p) => p.id === payload.playerId);
  const card = player.board.hand.find(c => c.id === payload.cardId);
  player.properties.mana -= card.properties.manaCost;
  return (state) => {
    events.discardCard(state, payload);
  };
});

define((state, {playerId, cardId}) => {
  const player = state.players.find((p) => p.id === playerId);
  const {hand, discard} = player.board;
  const index = hand.findIndex(c => c.id === cardId);
  if (index !== -1) {
    const [card] = hand.splice(index, 1);
    discard.push(card);
  }
});

define((state, {player1Deck, player2Deck}) => {
  const [p1, p2] = state.players;
  p1.deckId = player1Deck;
  p2.deckId = player2Deck;

  for (const player of state.players) {
    const {board, properties} = player;
    const deck = state.decks.find(d => d.id === player.deckId);

    properties.mana = properties.manaMax;
    properties.health = properties.healthMax;
    
    board.hand = [];
    board.discard = [];
    board.draw = [];

    deck.cards.forEach((card) => {
      let count = card.properties.instanceCount;
      while (count--) {
        board.draw.push(cloneCard(card));
      }
    });

    events.reshuffle(state, player.id);
  }

  state.properties.currentPlayerId = state.players[0].id;
  state.properties.status = { type: "battle" };
  events.startTurn(state);
})

function logPlayerHealth(desc: string, player: Player) {
  const {health, healthMax} = player.properties;
  log(desc, player.id, health, "/", healthMax);
}

define((state, playerId) => {
  const player = state.players.find((p) => p.id === playerId);
  const {draw, hand, discard} = player.board;
  if (draw.length === 0 && discard.length > 0) {
    events.reshuffle(state, playerId);
  }
  const [card] = draw.splice(0, 1);
  if (card) {
    hand.push(card);
  }
})

define((state, playerId) => {
  const player = state.players.find(p => p.id === playerId);
  const {board} = player;
  board.draw = randomized([...board.discard, ...board.draw]);
  board.discard = [];

  function randomized <T>(items: T[]) {
    const left = [...items];
    const rand = [];
    while(left.length) {
      const index = Math.round(Math.random() * (left.length - 1));
      const [item] = left.splice(index, 1);
      rand.push(item);
    }

    return rand;
  }
});

define((state) => {
  const {players, properties: { currentPlayerId }} = state;
  const player = players.find(p => p.id === currentPlayerId);
  const { discard, hand } = player.board;
  discard.push(...hand);
  player.board.hand = [];
  
  const index = players.findIndex(p => p.id === currentPlayerId);
  const nextPlayerIndex = (index + 1) % state.players.length;
  const nextPlayer = players[nextPlayerIndex];
  state.properties.currentPlayerId = nextPlayer.id;

  events.startTurn(state);
});

define((state) => {
  const player = state.players.find(p => p.id === state.properties.currentPlayerId);
  player.properties.mana = player.properties.manaMax;
  let drawCount = player.properties.drawCount;
  while (drawCount--) {
    events.drawCard(state, player.id);
  }
});

