define((state) => {
  for (const {properties} of state.players) {
    properties.health = clamp(properties.health, 0, properties.healthMax);
    properties.mana = clamp(properties.mana, 0, properties.manaMax);
  }
})

function clamp(v: number, min: number, max: number) {
  return Math.min(max, Math.max(v, min));
}

define((state) => {
  if (state.properties.status.type === "battle") {
    for (const player of state.players) {
      if (player.properties.health <= 0) {
        const otherPlayer = state.players.find(p => p !== player);
        state.properties.status = {type: "result", winner: otherPlayer.id};
        break;
      }
    }
  }
});
